    private void numbersaction(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_numbersaction
         switch(evt.getActionCommand()){
         case"0":
             if(TEXT.getText().length()>0){
             TEXT.setText(TEXT.getText()+"0");}
             break;
             case"1":
                 TEXT.setText(TEXT.getText()+"1");
                 break;
                 case"2":
                     TEXT.setText(TEXT.getText()+"2");
                     break;
                     case"3":
                         TEXT.setText(TEXT.getText()+"3");
                         break;
                         case"4":
                            TEXT.setText(TEXT.getText()+"4");
                            break;
                             case"5":
                                TEXT.setText(TEXT.getText()+"5");
                              break;
                                case"6":
                                   TEXT.setText(TEXT.getText()+"6");
                                   break;
                                   case"7":
                                      TEXT.setText(TEXT.getText()+"7");
                                      break;
                                      case"8":
                                         TEXT.setText(TEXT.getText()+"8");
                                         break;
                                         case"9":
                                            TEXT.setText(TEXT.getText()+"9");
                                            break;
         }
    }//GEN-LAST:event_numbersaction

    private void b_dotActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_b_dotActionPerformed
     if(!TEXT.getText().contains(".")) {
         TEXT.setText(TEXT.getText()+".");
     }  
    }//GEN-LAST:event_b_dotActionPerformed

    private void b_equalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_b_equalActionPerformed
      if(TEXT.getText().length()>0){
      n2=Double.parseDouble(TEXT.getText());
      }else{
          n2=n1;
      }
      double result;
      if(oper!='\u0000'){
      switch(oper){
          case'+':
              result=n1+n2;
              TEXT.setText(String.valueOf(result));
              label.setText(label.getText()+n2+" =");
              break;
              case'-':
                  result=n1-n2;
                  TEXT.setText(String.valueOf(result));
                  label.setText(label.getText()+n2+" =");
                  break;
                  case'x':
                      result=n1*n2;
                      TEXT.setText(String.valueOf(result));
                      label.setText(label.getText()+n2+" =");
                  break;    
                  case'รท':
                          result=n1/n2;
                          TEXT.setText(String.valueOf(result));
                          label.setText(label.getText()+n2+" =");
                        break;
      }
      oper='\u0000';
      }
      else 
          TEXT.setText(n2+"");
    }//GEN-LAST:event_b_equalActionPerformed

    private void b_clearActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_b_clearActionPerformed
        TEXT.setText("");
        label.setText("");
    }//GEN-LAST:event_b_clearActionPerformed

    private void operator(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_operator
       if(TEXT.getText().length()>0){
        switch(evt.getActionCommand()){
           case"+":
               n1=Double.parseDouble(TEXT.getText());
               oper='+';
               label.setText(n1+ " + ");
               TEXT.setText("");
               break;
               case"-":
               n1=Double.parseDouble(TEXT.getText());
               oper='-';
               label.setText(n1+ " - ");
               TEXT.setText("");
               break;
                case"x":
               n1=Double.parseDouble(TEXT.getText());
               oper='x';
               label.setText(n1+ " x ");
               TEXT.setText("");
               break;
                case"รท":
               n1=Double.parseDouble(TEXT.getText());
               oper='รท';
               label.setText(n1+ " รท ");
               TEXT.setText("");
               break;
       }
       }
    }//GEN-LAST:event_operator

    private void b_deleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_b_deleteActionPerformed
       String numbers=TEXT.getText().substring(0, TEXT.getText().length()-1);
       TEXT.setText(numbers);
    }//GEN-LAST:event_b_deleteActionPerformed

    private void b_negateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_b_negateActionPerformed
        TEXT.setText(-1*Double.parseDouble(TEXT.getText())+"");
    }//GEN-LAST:event_b_negateActionPerformed

